package c.g.a.x.lib_ali;import android.text.TextUtils;import java.util.Map;public final class AliResult {    private String resultStatus;    private String result;    private String memo;    private String resultCode;    private String authCode;    private String alipayOpenId;    private String userId;    public AliResult(Map<String, String> rawResult) {        if (rawResult == null) {            return;        }        for (String key : rawResult.keySet()) {            if (TextUtils.equals(key, "resultStatus")) {                resultStatus = rawResult.get(key);            } else if (TextUtils.equals(key, "result")) {                result = rawResult.get(key);            } else if (TextUtils.equals(key, "memo")) {                memo = rawResult.get(key);            }        }        if (TextUtils.isEmpty(result)) return;        String[] resultValue = result.split("&");        if (resultValue == null || resultValue.length <= 0) return;        for (String value : resultValue) {            if (value.startsWith("alipay_open_id")) {                alipayOpenId = removeBrackets(getValue("alipay_open_id=", value));                continue;            }            if (value.startsWith("auth_code")) {                authCode = removeBrackets(getValue("auth_code=", value));                continue;            }            if (value.startsWith("result_code")) {                resultCode = removeBrackets(getValue("result_code=", value));                continue;            }            if (value.startsWith("user_id")) {                userId = removeBrackets(getValue("user_id=", value));                continue;            }        }    }    private String removeBrackets(String str) {        if (TextUtils.isEmpty(str)) return str;        if (str.startsWith("\"")) str = str.replaceFirst("\"", "");        if (str.endsWith("\"")) str = str.substring(0, str.length() - 1);        return str;    }    private String getValue(String header, String data) {        return data.substring(header.length());    }    @Override    public String toString() {        return "resultStatus={" + resultStatus + "};memo={" + memo + "};result={" + result + "}";    }    /**     * @return the resultStatus     */    public String getResultStatus() {        return resultStatus;    }    /**     * @return the memo     */    public String getMemo() {        return memo;    }    /**     * @return the result     */    public String getResult() {        return result;    }    /**     * @return the resultCode     */    public String getResultCode() {        return resultCode;    }    /**     * @return the authCode     */    public String getAuthCode() {        return authCode;    }    /**     * @return the alipayOpenId     */    public String getAlipayOpenId() {        return alipayOpenId;    }    public String getUserId() {        return userId;    }}